import React, { Component } from "react";
import api from "../api";

import styled from "styled-components";

const Title = styled.h1.attrs({
  className: "h1",
})``;

const Wrapper = styled.div.attrs({
  className: "form-group",
})`
  margin: 0 30px;
`;

const Label = styled.label`
  margin: 5px;
`;

const InputText = styled.input.attrs({
  className: "form-control",
})`
  margin: 5px;
`;

const Button = styled.button.attrs({
  className: `btn btn-primary`,
})`
  margin: 15px 15px 15px 5px;
`;

const CancelButton = styled.a.attrs({
  className: `btn btn-danger`,
})`
  margin: 15px 15px 15px 5px;
`;

class QuizzesInsert extends Component {
  constructor(props) {
    super(props);

    this.state = {
      name: "",
      author: "",
      question: "",
      author: "",
    };
  }

  handleChangeInputName = async (event) => {
    const name = event.target.value;
    this.setState({ name });
  };

  handleChangeInputAuthor = async (event) => {
    const author = event.target.validity.valid
      ? event.target.value
      : this.state.author;

    this.setState({ author });
  };

  handleChangeInputQuestion = async (event) => {
    const author = event.target.validity.valid
      ? event.target.value
      : this.state.question;

    this.setState({ question });
  };

  handleChangeInputAnswer = async (event) => {
    const author = event.target.validity.valid
      ? event.target.value
      : this.state.answer;

    this.setState({ answer });
  };

  handleIncludeQuiz = async () => {
    const { name, author, question, answer } = this.state;
    const payload = { name, author, question, answer  };

    await api.insertQuiz(payload).then((res) => {
      window.alert(`Quiz inserted successfully`);
      this.setState({
        name: "",
        author: "",
        question: "",
        answer: "",
      });
    });
  };

  render() {
    const { name, author } = this.state;
    return (
      <Wrapper>
        <Title>Create Quiz</Title>

        <Label>Name: </Label>
        <InputText
          type="text"
          value={name}
          onChange={this.handleChangeInputName}
        />

        <Label>Author: </Label>
        <InputText
          type="text"
          value={author}
          onChange={this.handleChangeInputAuthor}
        />

        <Label>Question: </Label>
        <InputText
          type="text"
          value={question}
          onChange={this.handleChangeInputQuestion}
        />

        <Label>Answer: </Label>
        <InputText
          type="text"
          value={answer}
          onChange={this.handleChangeInputAnswer}
        />

        <Button onClick={this.handleIncludeQuiz}>Add Quiz</Button>
        <CancelButton href={"/quizzes/list"}>Cancel</CancelButton>
      </Wrapper>
    );
  }
}

export default QuizzesInsert;
